    private void btRemoverMedicoOKActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btRemoverMedicoOKActionPerformed
        Medico medico;
        
        String crm = textRemoverMedicoCRM.getText();
        ManipuladorMedicos manipMedico = new ManipuladorMedicos();
        
        if (crm.isEmpty()) {
            JOptionPane.showMessageDialog(rootPane, "Digite um crm.",
                    "Busca", JOptionPane.INFORMATION_MESSAGE);
        } else {
            
            medico = manipMedico.buscaPorCrm(Integer.parseInt(crm));

            if (medico != null ) {
                medicoEncontrado = medico;
                mostraDados();          
            } else {
                JOptionPane.showMessageDialog(rootPane, "Não encontrado!",
                        "Busca", JOptionPane.INFORMATION_MESSAGE);
            }
        }
    }//GEN-LAST:event_btRemoverMedicoOKActionPerformed

