    private void verificaRemoverNovamente() {
        int respUsuario = JOptionPane.showConfirmDialog(rootPane,
                "Remoção realizada com sucesso! Deseja remover outro "
                + "médico?", "Sucesso!", JOptionPane.YES_NO_OPTION);
        switch (respUsuario) {
            case JOptionPane.YES_OPTION:
                btRemoverMedicoLimpar.doClick();
                break;
            case JOptionPane.NO_OPTION:
                btRemoverMedicorVoltar.doClick();
                break;
            default:
                btRemoverMedicorVoltar.doClick();
                break;
        }
    }
    private void limpaCampos() {
        textRemoverMedicoCRM.setText("");
        textMedicoRemover.setText("");
        
} 
    private void mostraDados() {
    textMedicoRemover.setText("NOME: "+ medicoEncontrado.getNome() +"\n" +
    "CRM: "  + medicoEncontrado.getCrm()+ "\n" +
    "ESPECIALIZAÇÃO: "+ medicoEncontrado.getEspecializacao()+ "\n" +
    "TELEFONE: " + medicoEncontrado.getTelefone()+ "\n");    
       
 }
